package com.victor.player.library.presenter;import com.victor.http.annotation.HttpParms;import com.victor.http.data.Request;import com.victor.http.inject.HttpInject;import com.victor.http.module.HttpRequest;import com.victor.http.presenter.impl.BasePresenterImpl;import com.victor.player.library.view.SubTitleListView;/** * Created by victor on 2017/2/8. * 获取Youtube字幕列表 Prestener实现 */public class SubTitleListPresenterImpl<H,T> extends BasePresenterImpl<H,T> {	/*Presenter作为中间层，持有View和Model的引用*/	private SubTitleListView subTitleListView;	public SubTitleListPresenterImpl(SubTitleListView subTitleListView) {		this.subTitleListView = subTitleListView;	}	@Override	public void onSuccess(T data) {		if (subTitleListView == null) return;		if (data == null) {			subTitleListView.OnSubTitleList(null,"no data response");		} else {			subTitleListView.OnSubTitleList(data,"");		}	}	@Override	public void onError(String error) {		if (subTitleListView == null) return;		subTitleListView.OnSubTitleList(null,error);	}	@Override	public void detachView() {		subTitleListView = null;	}	@HttpParms(method = Request.GET,bodyContentType = HttpRequest.mJsonBodyContentType,			responseCls = String.class,httpFramework = HttpRequest.OKHTTP_FRAMEWORK)	@Override	public void sendRequest(String url,H header,T parm) {		HttpInject.inject(this);		super.sendRequest(url,header,parm);	}}