package com.victor.player.library.presenter;import com.android.volley.Request;import com.victor.http.annotation.HttpParms;import com.victor.http.inject.HttpInject;import com.victor.http.module.VolleyRequest;import com.victor.http.presenter.impl.BasePresenterImpl;import com.victor.player.library.view.SubTitleView;/** * Created by victor on 2017/2/8. * 获取Youtube字幕 Prestener实现 */public class SubTitlePresenterImpl<H,T> extends BasePresenterImpl<H,T> {	/*Presenter作为中间层，持有View和Model的引用*/	private SubTitleView subTitleView;	public SubTitlePresenterImpl(SubTitleView subTitleView) {		this.subTitleView = subTitleView;	}	@Override	public void onSuccess(T data) {		if (subTitleView == null) return;		if (data == null) {			subTitleView.OnSubTitle(null,"no data response");		} else {			subTitleView.OnSubTitle(data,"");		}	}	@Override	public void onError(String error) {		if (subTitleView == null) return;		subTitleView.OnSubTitle(null,error);	}	@Override	public void detachView() {		subTitleView = null;	}	@HttpParms(method = Request.Method.GET,bodyContentType = VolleyRequest.mJsonBodyContentType,responseCls = String.class)	@Override	public void sendRequest(String url,H header,T parm) {		HttpInject.inject(this);		super.sendRequest(url,header,parm);	}}