package com.victor.player.library.presenter;import com.android.volley.Request;import com.victor.http.annotation.HttpParms;import com.victor.http.inject.HttpInject;import com.victor.http.module.VolleyRequest;import com.victor.http.presenter.impl.BasePresenterImpl;import com.victor.player.library.view.VimeoView;/** * Created by victor on 2017/2/8. * 获取vimeo播放地址 Prestener实现 */public class VimeoPresenterImpl<H,T> extends BasePresenterImpl<H,T> {	/*Presenter作为中间层，持有View和Model的引用*/	private VimeoView vimeoView;	public VimeoPresenterImpl(VimeoView vimeoView) {		this.vimeoView = vimeoView;	}	@Override	public void onSuccess(T data) {		if (vimeoView == null) return;		if (data == null) {			vimeoView.OnVimeo(null,"no data response");		} else {			vimeoView.OnVimeo(data,"");		}	}	@Override	public void onError(String error) {		vimeoView.OnVimeo(null,error);	}	@Override	public void detachView() {		vimeoView = null;	}	@HttpParms(method = Request.Method.GET,bodyContentType = VolleyRequest.mJsonBodyContentType,responseCls = String.class)	@Override	public void sendRequest(String url,H header,T parm) {		HttpInject.inject(this);		super.sendRequest(url,header,parm);	}}