package com.victor.player.library.presenter;import com.android.volley.Request;import com.victor.http.annotation.HttpParms;import com.victor.http.inject.HttpInject;import com.victor.http.module.VolleyRequest;import com.victor.http.presenter.impl.BasePresenterImpl;import com.victor.player.library.view.YoutubeCheckView;import com.victor.player.library.view.YoutubeView;/** * Created by victor on 2017/2/8. * youtube节目 播放检测 Prestener实现 */public class YoutubeCheckPresenterImpl<H,T> extends BasePresenterImpl<H,T> {	/*Presenter作为中间层，持有View和Model的引用*/	private YoutubeCheckView youtubeCheckView;	public YoutubeCheckPresenterImpl(YoutubeCheckView youtubeCheckView) {		this.youtubeCheckView = youtubeCheckView;	}	@Override	public void onSuccess(T data) {		if (youtubeCheckView == null) return;		if (data == null) {			youtubeCheckView.OnYoutubeCheck(null,"no data response");		} else {			youtubeCheckView.OnYoutubeCheck(data,"");		}	}	@Override	public void onError(String error) {		youtubeCheckView.OnYoutubeCheck(null,error);	}	@Override	public void detachView() {		youtubeCheckView = null;	}	@HttpParms(method = Request.Method.GET,bodyContentType = VolleyRequest.mDefaultBodyContentType,responseCls = String.class)	@Override	public void sendRequest(String url,H header,T parm) {		HttpInject.inject(this);		super.sendRequest(url,header,parm);	}}